/**
 * Retrieves a specified number of questions for a given topic.
 * If the exact number isn't available, it returns all available questions for that topic.
 * If the topic is not found, it returns an empty array.
 * @param topic The topic of the quiz.
 * @param count The desired number of questions.
 * @returns An array of GeneratedQuestion.
 */
export function getPredefinedQuestions(
  topic: string,
  count: number = 20
): GeneratedQuestion[] {
  const questionsForTopic = predefinedQuizData[topic];
  if (!questionsForTopic || questionsForTopic.length === 0) {
    console.warn(`No predefined questions found for topic: ${topic}`);
    return [];
  }

  // Shuffle questions to provide variety
  const shuffledQuestions = [...questionsForTopic].sort(() => 0.5 - Math.random());

  return shuffledQuestions.slice(0, count);
}
